sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {any} params.imports
  */
function evaluateProperty({api}) {
	var val = api.data.projectstatus_gliderecord.output.data.GlideRecord_Query.tsp1_project_status._results[0].cost.value;
  if(val=='green'){
    return 'positive';
  }
  else if(val=='yellow'){
    return 'warning';
  }
  else if(val=='red'){
    return 'critical';
  }
  else{
    return '';
  }
}
